import React from 'react';
 

const addUser = ({handler,add}) => {
    return(
        <div className='pa2'>
        
        <input
          id="mainInput"
          ref={(ref) => this.mainInput= ref}
          className='pa3 ba b--green bg-lightest-blue'
          type='text'
          name='name'
          placeholder='Name'
          onChange={handler}
         
          
        />
        <input
          className='pa3 ba b--green bg-lightest-blue'
          type='text'
          name='userName'
          placeholder='User Name'
          onChange={handler}
          
        />
        <input
          className='pa3 ba b--green bg-lightest-blue'
          type='email'
          name='email'
          placeholder='Email'
          onChange={handler}
          
        />
        <button onClick={add}>Sumbet</button>
        
    </div>
    );
}

export default addUser;
