import React, { Component } from 'react';
import CardArray from './CardArray'
import {robots} from './Robots'
import SearchBox from './SearchBox'

class App extends Component {
    constructor(){
        super();
        this.state = {
            robots : robots,
            searchfield: ''
        }
    }
    render() {
        return (
            <div className='tc'>
                <h1>RoboFriends</h1>
                <SearchBox />
                <CardArray robots={robots}/>
            </div>
            
        );
    }
}

export default App;
